import React from 'react';
import styles from "./App.module.css"
import { Square } from './Square/Square';
import {io} from "socket.io-client"
import swal from 'sweetalert2';
import { useState, useEffect } from 'react';

const renderFrom = [
  [1, 2, 3],
  [4, 5, 6],
  [7, 8, 9],
];

function App() {
  const [gameState, setGameState] = useState(renderFrom);
  const [currentPlayer, setCurrentPlayer] = useState("circle");
  const [playOnline, setPlayOnline] = useState(false);
  const [playerName, setPlayerName] = useState("");
  const [socket, setSocket] = useState(null);
  const [finishedState, setFinishedState] = useState(false);
  const [finishedArrayState, setFinishedArrayState] = useState([]);
  const [opponentName, setOpponentName] = useState(null);
  const [playingAs, setPlayingAs] = useState(null);

  const checkWinner = () => {
    // row dynamic 
    for(let row=0; row < gameState.length; row++){
      if(gameState[row][0] === gameState[row][1] && gameState[row][1] === gameState[row][2]){
        setFinishedArrayState([row*3+0, row*3+1, row*3+2])
        return gameState[row][0];
      }
    }

    // column dynamic 
    for(let col=0; col<gameState.length; col++){
      if(gameState[0][col] === gameState[1][col] && gameState[1][col] === gameState[2][col]){
        setFinishedArrayState([0*3 + col, 1*3 + col, 2*3 + col])
        return gameState[0][col];
      }
    }

    if(gameState[0][0] === gameState[1][1] && gameState[1][1] === gameState[2][2]){
      return gameState[0][0];
    }

    if(gameState[0][2] === gameState[1][1] && gameState[1][1] === gameState[2][0]){
      return gameState[0][2];
    }

    const isDrawMatch = gameState.flat().every((e)=>{
      if(e === 'circle' || e === 'cross')return true;
    })

    if(isDrawMatch) return "draw";

    return null;
  }

  useEffect(()=> {
    const winner = checkWinner();
    if(winner){
      setFinishedState(winner);
    }
  }, [gameState]);

  const takePlayerName = async ()=> {
    const result = await swal({
      title: "Enter your name",
      input: "text",
      showCancleButton: true,
      inputValidator: (value) => {
        if(!value) {
          return "You need to write something!";
        }
      },
    }).then(function (){
      if(playOnline && !opponentName){
        console.log(playOnline);
        console.log(opponentName);
        return (
          <div className={styles.waiting}>
            <p>Waiting for opponent</p>
          </div>
        )
      }
    })

    return result;
  };

  socket?.on("opponentLeftMatch", ()=>{
    setFinishedState("opponentLeftMatch");
  })

  socket?.on("playerMoveFromServer", (data)=> {
    const id = data.state.id;
    setGameState((prevState)=> {
      let newState = [...prevState];
      const rowIndex = Math.floor(id/3);
      const colIndex = id %3;
      newState[rowIndex][colIndex] = data.state.sign;
      return newState;
    })
    setCurrentPlayer(data.state.sign === 'circle' ? 'cross' :"circle");
  })

  socket?.on("connect", function(){
    setCurrentPlayer(true);
  })

  socket?.on("opponentNotFound", function(){
    setOpponentName(false);
  })

  socket?.on("opponetFound", function(data){
    setPlayingAs(data.playingAs);
    setOpponentName(data.opponentName);
  })

  async function playOnlineClick(){
    const result = await takePlayerName();

    if(!result.isConfirmed){
      return ;
    }
    const username = result.value;
    setPlayerName(username);

    const newSocket = io("http://localhost:3000", {
      autoConnect: true,
    })

    newSocket?.emit("request_to_play", {
      playerName: username,
    });

    setSocket(newSocket);
  }

  if(!playOnline){
    return(
      <div className={styles.main}>
        <button onClick={playOnlineClick} className={styles.playOnline}>
          Play Online
        </button>
      </div>
    )
  }

  if(playOnline && !opponentName){
    console.log(playOnline);
    console.log(opponentName);
    return (
      <div className={styles.waiting}>
        <p>Waiting for opponent</p>
      </div>
    )
  }

  return (
    <>
    <div className={styles.container}>
      <div className={styles.moveDetection}>
        <div className={`${styles.left} ${currentPlayer === playingAs ? `${styles.move}`+ currentPlayer : ""}`}>
          {playerName}
        </div>
        <div className={`${styles.right} ${currentPlayer === playingAs ? `${styles.move}`+ currentPlayer : ""}`}>
          {opponentName}
        </div>
      </div>
      <div className={styles.heading}>
        <h1>Tic Tac Toe</h1>
        <div className={styles.squareWrapper}>
          {gameState.map((arr, rowIndex)=> 
        arr.map((e, colIndex)=>{
          return <Square/>
        }))}
        </div>
      </div>
      {!finishedState && opponentName && (
        <h2>You are playing against {opponentName}</h2>
      )}
      {finishedState && finishedState === "opponentLeftMatch" && (
        <h2>You won the match, Opponent has left</h2>
      )}
    </div>
    </>
  );
}

export default App;
