      </div>
      <div className={styles.heading}>
        <h1>Tic Tac Toe</h1>
        <div className={styles.squareWrapper}>
          {gameState.map((arr, rowIndex)=> 
        arr.map((e, colIndex)=>{
          return <Square/>
